// Signal handling para binarios PKG
function setupSignalHandlers() {
  const gracefulShutdown = (signal) => {
    console.log(`\nðŸ›‘ Received ${signal}. Shutting down JoRA...`);

    // Cerrar servidor Express
    if (global.joraServer) {
      global.joraServer.close(() => {
        console.log('âœ… Server closed successfully');
        process.exit(0);
      });

      // Timeout para forzar salida
      setTimeout(() => {
        console.log('âš ï¸ Force closing JoRA...');
        process.exit(1);
      }, 5000);
    } else {
      process.exit(0);
    }
  };

  process.on('SIGINT', () => gracefulShutdown('SIGINT'));
  process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));

  // PKG specific handling
  if (process.pkg) {
    console.log('ðŸ”§ PKG binary detected - enhanced signal handling enabled');
  }
}
```

